import React from "react";

const Tree = (props) => {
  return (
    <div className="flow-container">
      <div className="central-node">{props.address}</div>
      {[...props.tree].map((tx, i) => (
        <div
          id={i}
          onClick={(e) => {
            props.triggerFetch(e.target.innerHTML);
          }}
          style={{
            position: "absolute",
            left: `calc(50% - ${i + 1 * 2})`,
            transform: "translateX(-50%)",
            top: `calc(50% - ${i + 1 * 2})`,
          }}
        >
          {tx}
        </div>
      ))}
    </div>
  );
};

export default Tree;
