import React, { useRef } from "react";

const Input = (props) => {
  const inputRef = useRef();

  return (
    <div>
      <input type="text" placeholder="Ethereum Address" ref={inputRef} />
      <button
        type="submit"
        onClick={() => {
          props.triggerFetch(inputRef.value);
        }}
      >
        Submit
      </button>
    </div>
  );
};

export default Input;
