import React, { useState, useEffect } from "react";
import ReactFlow from "react-flow-renderer";

/* {[...props.tree].map((tx, i) => (
  <div
    id={i}
    onClick={(e) => {
      props.triggerFetch(e.target.innerHTML);
    }} */
const initialNodes = [
  {
    id: "1",
    type: "input",
    data: { label: "Query an ETH Address to load a tree" },
    position: { x: 250, y: 25 },
  },
];

const initialEdges = [];

const Tree = (props) => {
  const [nodes, setNodes] = useState(initialNodes);
  const [edges, setEdges] = useState(initialEdges);

  useEffect(() => {
    const newNodes = [
      {
        id: `a`,
        type: "input",
        data: { label: `${props.address}` },
        position: { x: Math.random() * 500, y: Math.random() * 500 },
      },
    ];
    const newEdges = [];

    [...props.tree].forEach((tx, i) =>
      newNodes.push({
        id: `${i}`,
        type: "output",
        data: { label: `${tx}` },
        position: { x: Math.random() * 500, y: Math.random() * 500 },
      })
    );

    [...props.tree].forEach((tx, i) =>
      newEdges.push({ id: `${tx}`, source: "a", target: `${i}` })
    );

    setNodes(newNodes);
    setEdges(newEdges);
  }, [props.tree]);

  return (
    <div className="flow-container">
      <ReactFlow nodes={nodes} edges={edges} fitView />
    </div>
  );
};

export default Tree;
