import React, { useEffect } from "react";
import Graph from "./Graph";

const Tree = (props) => {
  if (props.show == "Graph") {
    return (
      <Graph
        address={props.address}
        tree={props.tree}
        triggerFetch={props.triggerFetch}
      />
    );
  }

  if (props.show == "Loading") {
    return (
      <div className="load-circle">
        <svg
          xmlns="http://www.w3.org/2000/svg"
          width="100"
          height="100"
          viewBox="0 0 200 200"
          fill="none"
          color="#3f51b5"
        >
          <defs>
            <linearGradient id="spinner-secondHalf">
              <stop offset="0%" stop-opacity="0" stop-color="currentColor" />
              <stop
                offset="100%"
                stop-opacity="0.5"
                stop-color="currentColor"
              />
            </linearGradient>
            <linearGradient id="spinner-firstHalf">
              <stop offset="0%" stop-opacity="1" stop-color="currentColor" />
              <stop
                offset="100%"
                stop-opacity="0.5"
                stop-color="currentColor"
              />
            </linearGradient>
          </defs>
          <g stroke-width="8">
            <path
              stroke="url(#spinner-secondHalf)"
              d="M 4 100 A 96 96 0 0 1 196 100"
            />
            <path
              stroke="url(#spinner-firstHalf)"
              d="M 196 100 A 96 96 0 0 1 4 100"
            />
            <path
              stroke="currentColor"
              stroke-linecap="round"
              d="M 4 100 A 96 96 0 0 1 4 98"
            />
          </g>
          <animateTransform
            from="0 0 0"
            to="360 0 0"
            attributeName="transform"
            type="rotate"
            repeatCount="indefinite"
            dur="1300ms"
          />
        </svg>
      </div>
    );
  }

  if (props.show == "Error") {
    return (
      <div className="error-text">
        You may not have provided a valid address. Try searching again.
      </div>
    );
  }

  if (props.show == "Info") {
    return (
      <div className="flavour-text">
        - Input any valid 0x address into the search bar to begin exploring the
        blockchain.
        <br></br>
        <br></br>- You can click on a node to explore that addresses transaction
        history.
        <br></br>
        <br></br>- API Queries may take an extra moment to load for a
        particularily active address.
      </div>
    );
  }
};

export default Tree;
