import React, { useState, useEffect } from "react";
import { ethers } from "ethers";

const Input = () => {
  const [balance, setBalance] = useState(0);

  useEffect(() => {
    async function fetchBalance(address) {
      const provider = new ethers.providers.EtherscanProvider(
        "homestead",
        "GA5DBDU9R58E28EUTCVD9KA3H3FXBP5MH94"
      );

      const historyGetter = await provider.getHistory(address);
      let txList = new Set();
      historyGetter.forEach((tx) => {
        txList.add(tx.from == parseInt(address) ? tx.to : tx.from);
      });
    }

    fetchBalance("0xa6c04aaf92dafe8fed6d2e9d429ca0dba5539948");
    console.log(txList);
  });

  return (
    <div>
      <div>{balance}</div>
    </div>
  );
};

export default Input;
