import React, { useEffect } from "react";
import Graph from "./Graph";

const Tree = (props) => {
  if (props.show == "Graph") {
    return (
      <Graph
        address={props.address}
        tree={props.tree}
        triggerFetch={props.triggerFetch}
      />
    );
  }

  if (props.show == "Loading") {
    return <div>Loading</div>;
  }

  if (props.show == "Info") {
    return (
      <div>
        Input any valid 0x address into the search bar to begin exploring the
        blockchain.
        <br></br>
        You can click on a node to explore that addresses transaction history.
        <br></br>
        API Queries may take an extra moment to load for a particularily active
        address.
      </div>
    );
  }
};

export default Tree;
