import React from "react";
import Xarrow from "react-xarrows";

function getRandomInt(min, max) {
  min = Math.ceil(min);
  max = Math.floor(max);
  return Math.floor(Math.random() * (max - min) + min);
}

function renderNodes(array, triggerFunction) {
  [array].map((tx, i) => (
    <div
      className="node-wrapper"
      id={tx}
      onClick={(e) => {
        triggerFunction(e.target.id);
      }}
      style={{
        position: "absolute",
        left: `${getRandomInt(20, 80)}%`,
        transform: "translateX(-50%)",
        top: `${getRandomInt(20, 80)}%`,
      }}
    >
      <div className="address-node">{tx}</div>
    </div>
  ));
}

const Tree = (props) => {
  return (
    <div className="flow-container">
      <div className="central-node" id="a">
        {props.address}
      </div>
      {[...props.tree].map((tx, i) => (
        <div
          className="node-wrapper"
          id={tx}
          onClick={(e) => {
            props.triggerFetch(e.target.id);
          }}
          style={{
            position: "absolute",
            left: `${getRandomInt(20, 80)}%`,
            transform: "translateX(-50%)",
            top: `${getRandomInt(20, 80)}%`,
          }}
        >
          <div className="address-node">{tx}</div>
        </div>
      ))}
      {[...props.tree].map((tx, i) => (
        <Xarrow
          start="a" //can be react ref
          end={tx} //or an id
          startAnchor="middle"
          endAnchor="middle"
          showHead={false}
          strokeWidth={1}
          curveness={0}
          lineColor="#0076ED"
        />
      ))}
    </div>
  );
};

export default Tree;
