import React, { useState, useEffect } from "react";
import { ethers } from "ethers";
import "./App.css";
import Input from "./components/Input";
import Tree from "./components/Tree.js";

function App() {
  const [tree, setTree] = useState("");
  const [address, setAddress] = useState("");
  const [show, setShow] = useState("Info");

  useEffect(() => {
    async function fetchHistory(address) {
      const provider = new ethers.providers.EtherscanProvider(
        "homestead",
        "GA5DBDU9R58E28EUTCVD9KA3H3FXBP5MH94"
      );

      const historyGetter = await provider.getHistory(address);
      const txList = new Set();
      historyGetter.forEach((tx) => {
        txList.add(tx.from === address ? tx.to : tx.from);
      });

      setTree([...txList].slice(0, 20));
      setShow("Graph");
    }

    fetchHistory(address);
  }, [address]);

  async function triggerFetch(newAddress) {
    setShow("Loading");
    const checksumAddress = await ethers.utils.getAddress(newAddress);
    setAddress(newAddress);
  }

  return (
    <div className="page-container">
      <Input triggerFetch={triggerFetch} />
      <Tree
        show={show}
        tree={tree}
        triggerFetch={triggerFetch}
        address={address}
      />
    </div>
  );
}

export default App;
