import React from "react";
import Xarrow from "react-xarrows";

function getRandomInt(min, max, exclude) {
  if (!exclude) {
    min = Math.ceil(min);
    max = Math.floor(max);
    return Math.floor(Math.random() * (max - min) + min);
  }

  if (exclude) {
    min = Math.ceil(min);
    max = Math.floor(max);
    const low = Math.floor(Math.random() * (45 - min) + min);
    const high = Math.floor(Math.random() * (max - 55) + 55);

    return Math.random() < 0.5 ? low : high;
  }
}

const Graph = (props) => {
  return (
    <div className="flow-container">
      <div className="central-node" id="a">
        {props.address}
      </div>
      {[...props.tree].map((tx) => (
        <div
          className="node-wrapper"
          id={tx}
          onClick={(e) => {
            props.triggerFetch(e.target.id);
          }}
          style={{
            position: "absolute",
            left: `${getRandomInt(30, 70, false)}%`,
            transform: "translateX(-50%)",
            top: `${getRandomInt(30, 70, true)}%`,
          }}
        >
          <div className="address-node">{tx}</div>
        </div>
      ))}
      {[...props.tree].map((tx, i) => (
        <Xarrow
          start="a" //can be react ref
          end={tx} //or an id
          startAnchor="middle"
          endAnchor="middle"
          showHead={false}
          strokeWidth={1}
          curveness={0}
          lineColor="#0076ED"
        />
      ))}
    </div>
  );
};

export default Graph;
